import React, { useState } from 'react';
import { Link } from 'react-router-dom';  // Thêm dòng này để sử dụng Link
import { useCart } from '..context/CartContext'; // Import hook useCart
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductInfoPage() {
  // Lấy tham số productId từ URL
  // const { productId } = useParams();  
  
  const product = {
    id: 1,
    name: 'Peppermint Latte',
    description: 'A refreshing minty coffee!',
    price: 3,
    imageUrl: '/images/photo_2024-08-19_14-32-45.png',
    relatedProducts: [
      { id: 2, name: 'Product 7', price: 10, imageUrl: '/images/photo_2024-08-19_14-32-45.png' },
      { id: 3, name: 'Product 8', price: 10, imageUrl: '/images/photo_2024-08-19_14-32-45.png' },
    ]
  };

  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (type) => {
    setQuantity(prev => (type === 'increase' ? prev + 1 : prev > 1 ? prev - 1 : 1));
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">{product.name}</h1>
        <div className="row">
          <div className="col-md-6">
            <img src={product.imageUrl} className="img-fluid" alt={product.name} />
          </div>
          <div className="col-md-6">
            <p>{product.description}</p>
            <h3>${product.price}</h3>
            <div className="d-flex justify-content-between align-items-center">
              <button className="btn btn-danger" onClick={() => handleQuantityChange('decrease')}>-</button>
              <span>{quantity}</span>
              <button className="btn btn-success" onClick={() => handleQuantityChange('increase')}>+</button>
            </div>
            <button 
                    className="btn btn-primary"
                    onClick={() => addToCart(product)}  // Thêm sản phẩm vào giỏ
                  >
                    Add to Cart
                  </button>
            <Link to="/cart" className="btn btn-primary ml-3">View Cart</Link> {/* Chuyển đến giỏ hàng */}
            <h4>Related Products</h4>
            <div className="row">
              {product.relatedProducts.map((item) => (
                <div className="col-md-4 mb-4" key={item.id}>
                  <div className="card">
                    <img src={item.imageUrl} className="card-img-top" alt={item.name} />
                    <div className="card-body">
                      <h5 className="card-title">{item.name}</h5>
                      <p>${item.price}</p>
                      <Link to={`/product/${product.id}`} className="btn btn-primary">Select</Link> 
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductInfoPage;
