import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';  
import { useCart } from '../context/CartContext';
import 'bootstrap/dist/css/bootstrap.min.css';

function CartPage() {
  const { cart, updateQuantity, removeFromCart } = useCart();
  const [initialCartState, setInitialCartState] = useState(cart); // Track the initial state of the cart
  const [isCartModified, setIsCartModified] = useState(false); // State to track cart changes
  const navigate = useNavigate(); // For navigation to the checkout page

  useEffect(() => {
    // Check if cart has been modified
    const isModified = JSON.stringify(cart) !== JSON.stringify(initialCartState);
    setIsCartModified(isModified);
  }, [cart, initialCartState]);

  const handleQuantityChange = (id, type) => {
    updateQuantity(id, type); // Update quantity using CartContext
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleUpdateCart = () => {
    // Lưu các thay đổi của giỏ hàng nếu cần
    console.log('Giỏ hàng đã được cập nhật');
    setInitialCartState(cart); // Update initial state after changes
  };

  const handleRemoveItem = (id) => {
    removeFromCart(id); // Call removeFromCart from CartContext to remove the item
  };

  const handleProceedToCheckout = () => {
    navigate('/checkout', { state: { cart } }); // Pass the cart data to CheckoutPage
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">Cart</h1>
        <div className="row">
          {cart.map(item => (
            <div className="col-md-6 mb-4" key={item.id}>
              <div className="card">
                {/* <img src={item.imageUrl} className="card-img-top" alt={item.name} /> */}
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p>Price: ${item.price}</p>
                  <div className="d-flex justify-content-between">
                    <div>
                      <button className="btn btn-danger" onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
                      <span className="mx-2">{item.quantity}</span>
                      <button className="btn btn-success" onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
                    </div>
                    <p>Subtotal: ${item.price * item.quantity}</p>
                  </div>
                  <button className="btn btn-danger" onClick={() => handleRemoveItem(item.id)}>Remove</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="d-flex justify-content-between">
          <h3>Total: ${calculateTotal()}</h3>
          <div>
            <button 
              className="btn btn-primary mt-3" 
              onClick={handleUpdateCart}
              disabled={!isCartModified} // Disable if no changes
            >
              Update Cart
            </button>
            <button
              className="btn btn-success mt-3 ml-2"
              onClick={handleProceedToCheckout} // Navigate to checkout page
            >
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}


export default CartPage;
