import React, { useState, useEffect } from 'react';
import { useNavigate, Link   } from 'react-router-dom';  
import { useCart } from '../context/CartContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import InputNumber from 'rc-input-number';  // Import thư viện rc-input-number

function CartPage() {
  const { cart, updateQuantity, removeFromCart } = useCart();
  const [isUpdated, setIsUpdated] = useState(false);
  const [newQuantity, setNewQuantity] = useState({});  // Track new quantity for each item
  const navigate = useNavigate();  // Using useNavigate

  // Cập nhật lại localStorage mỗi khi có sự thay đổi
  useEffect(() => {
    const hasChanges = cart.some(item => item.quantity !== newQuantity[item.id]);
    setIsUpdated(hasChanges);
    // Lưu giỏ hàng vào localStorage khi có thay đổi
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [newQuantity, cart]);

  const handleQuantityChange = (id, type) => {
    updateQuantity(id, type);  
    setIsUpdated(true);  
  };

  const handleInputChange = (value, id) => {
    // Ensure value is at least 1 (prevent negative or zero values)
    const newValue = value === "" ? 1 : Math.max(1, parseInt(value));
    setNewQuantity(prev => ({ ...prev, [id]: newValue }));
    
    setIsUpdated(true);  // Mark as updated
  };

  const handleDelete = (id) => {
    removeFromCart(id);  
    setIsUpdated(true); 
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleProceedToCheckout = () => {
    // Lưu giỏ hàng vào localStorage trước khi chuyển sang trang checkout
    localStorage.setItem("cart", JSON.stringify(cart.map(item => ({
        ...item,
        quantity: newQuantity[item.id] || item.quantity
    }))));

    // Truyền giỏ hàng với số lượng đã được cập nhật tới CheckoutPage
    navigate('/checkout', { state: { cart: cart.map(item => ({
        ...item,
        quantity: newQuantity[item.id] || item.quantity
    })) } });
};

  const handleUpdateCart = () => {
    cart.forEach(item => {
      if (newQuantity[item.id] && newQuantity[item.id] !== item.quantity) {
        updateQuantity(item.id, newQuantity[item.id] - item.quantity);
      }
    });
    setIsUpdated(false);
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">Cart</h1>
        <div className="row">
          {cart.map(item => (
            <div className="col-md-6 mb-4" key={item.id}>
              <div className="card">
                <div className="card-body">
                  {/* Đổi tên sản phẩm thành liên kết */}
                  <Link to={`/product/${item.id}`} className="card-title">
                    <h5>{item.name}</h5>
                  </Link>
                  <p>Price: ${item.price}</p>

                  {/* Quantity Controls */}
                  <div className="d-flex justify-content-start align-items-center">
                    <button className="btn btn-danger" onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
                     <InputNumber
                      className="form-control mx-2"
                      value={newQuantity[item.id] !== undefined ? newQuantity[item.id] : item.quantity} // Ensure the correct value is displayed
                      onChange={(value) => handleInputChange(value, item.id)} // Use onChange for rc-input-number
                      min={1}
                    />
                    <button className="btn btn-success" onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
                  </div>

                  {/* Delete Button */}
                  <button className="btn btn-danger mt-2" onClick={() => handleDelete(item.id)}>
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Total and Proceed to Checkout */}
        <div className="d-flex justify-content-between">
          <h3>Subtotal: ${calculateTotal()}</h3>
          <div>
            {/* <button 
              className="btn btn-primary mt-3" 
              onClick={handleUpdateCart}
              disabled={!isUpdated} 
            >
              Update Cart
            </button> */}
            <button
              className="btn btn-success mt-3 ml-2"
              onClick={handleProceedToCheckout}
            >
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CartPage;
