import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';  
import { useCart } from '../context/CartContext';
import 'bootstrap/dist/css/bootstrap.min.css';

function CartPage() {
  const { cart, updateQuantity, removeFromCart } = useCart();
  const [isUpdated, setIsUpdated] = useState(false);
  const [newQuantity, setNewQuantity] = useState({});  
  const navigate = useNavigate();

  useEffect(() => {
    const hasChanges = cart.some(item => item.quantity !== newQuantity[item.id]);
    setIsUpdated(hasChanges);
  }, [newQuantity, cart]);

  const handleQuantityChange = (id, type) => {
    updateQuantity(id, type); 
    setIsUpdated(true);
  };

  const handleInputChange = (e, id) => {
    const value = Math.max(1, parseInt(e.target.value) || 1);
    setNewQuantity(prev => ({ ...prev, [id]: value }));
    setIsUpdated(true);
  };

  const handleDelete = (id) => {
    removeFromCart(id); 
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleProceedToCheckout = () => {
    navigate('/checkout', { state: { cart } }); // Passing cart data to checkout page
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">Cart</h1>
        <div className="row">
          {cart.map(item => (
            <div className="col-md-6 mb-4" key={item.id}>
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p>Price: ${item.price}</p>

                  {/* Quantity Controls */}
                  <div className="d-flex justify-content-between align-items-center">
                    <div>
                      <button className="btn btn-danger" onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
                      <input
                        type="number"
                        className="form-control w-25 mx-2"
                        value={newQuantity[item.id] || item.quantity}
                        onChange={(e) => handleInputChange(e, item.id)}
                        min="1"
                      />
                      <button className="btn btn-success" onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
                    </div>
                    <p>Subtotal: ${item.price * (newQuantity[item.id] || item.quantity)}</p>
                  </div>

                  {/* Delete Button */}
                  <button 
                    className="btn btn-danger mt-2"
                    onClick={() => handleDelete(item.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Total and Proceed to Checkout */}
        <div className="d-flex justify-content-between">
          <h3>Total: ${calculateTotal()}</h3>
          <div>
            <button 
              className="btn btn-primary mt-3" 
              onClick={handleProceedToCheckout}
            >
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}


export default CartPage;
