import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './pages/HomePage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import CategoryPage from './pages/CategoryPage';
import { Link } from 'react-router-dom';  // Thêm dòng này để sử dụng Link
import Header from './components/Header';
import Footer from './components/Footer'; // Import Footer nếu cần

function App() {
  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/cart" element={<CartPage />} />
        <Route path="/checkout" element={<CheckoutPage />} />
        <Route path="/category/:categoryName" element={<CategoryPage />} /> {/* Thêm tuyến đường cho Category */}
      </Routes>
      <Footer /> {/* Thêm Footer */}
    </Router>
  );
}

export default App;
