import React, { useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';  
import { useCart } from '../context/CartContext';  
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductInfoPage() {
  const { productId } = useParams();  
  const navigate = useNavigate();
  const { addToCart } = useCart();
  
  // Mock data for products
  const products = [
    { id: 1, name: 'Peppermint Latte', description: 'A refreshing minty coffee!', price: 3, imageUrl: '/images/photo_2024-08-19_14-32-45.png' },
    { id: 2, name: 'Vanilla Latte', description: 'A smooth vanilla coffee.', price: 5, imageUrl: '/images/photo_2024-08-19_14-32-45.png' }
  ];

  const product = products.find(p => p.id === parseInt(productId));

  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (type) => {
    setQuantity(prev => (type === 'increase' ? prev + 1 : prev > 1 ? prev - 1 : 1));
  };

  const handleAddToCart = () => {
    addToCart({ ...product, quantity });
    navigate('/cart');  // Automatically navigate to CartPage after adding to cart
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">{product.name}</h1>
        <div className="row">
          <div className="col-md-6">
            <img src={product.imageUrl} className="img-fluid" alt={product.name} />
          </div>
          <div className="col-md-6">
            <p>{product.description}</p>
            <h3>${product.price}</h3>
            <div className="d-flex justify-content-between align-items-center">
              <button className="btn btn-danger" onClick={() => handleQuantityChange('decrease')}>-</button>
              <span>{quantity}</span>
              <button className="btn btn-success" onClick={() => handleQuantityChange('increase')}>+</button>
            </div>
            <button className="btn btn-primary mt-3" onClick={handleAddToCart}>Add to Cart</button>
            <Link to="/cart" className="btn btn-primary ml-3">View Cart</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductInfoPage;
