import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Link } from 'react-router-dom';  // Thêm dòng này để sử dụng Link
import 'bootstrap/dist/css/bootstrap.min.css';

const cartItems = [
  { id: 1, name: 'Product ABC', price: 10, quantity: 1, imageUrl: 'product1.jpg' },
  { id: 2, name: 'Product XYZ', price: 15, quantity: 2, imageUrl: 'product2.jpg' },
];

function CartPage() {
  const [cart, setCart] = useState(cartItems);

  const handleQuantityChange = (id, type) => {
    const newCart = cart.map(item => {
      if (item.id === id) {
        const updatedQuantity = type === 'increase' ? item.quantity + 1 : item.quantity - 1;
        return { ...item, quantity: updatedQuantity > 0 ? updatedQuantity : 1 };
      }
      return item;
    });
    setCart(newCart);
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <div>
      <div className="container">
        <h1 className="text-center my-4">Cart</h1>
        <div className="row">
          {cart.map(item => (
            <div className="col-md-6 mb-4" key={item.id}>
              <div className="card">
                <img src={item.imageUrl} className="card-img-top" alt={item.name} />
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p>Price: ${item.price}</p>
                  <div className="d-flex justify-content-between">
                    <div>
                      <button className="btn btn-danger" onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
                      <span className="mx-2">{item.quantity}</span>
                      <button className="btn btn-success" onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
                    </div>
                    <p>Subtotal: ${item.price * item.quantity}</p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="d-flex justify-content-between">
          <h3>Total: ${calculateTotal()}</h3>
          <Link to="/checkout" className="btn btn-success mt-3">Proceed to Checkout</Link>
        </div>
      </div>
    </div>
  );
}

export default CartPage;
