import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';  
import { useCart } from '../context/CartContext';
import 'bootstrap/dist/css/bootstrap.min.css';

function CartPage() {
  const { cart, updateQuantity, removeFromCart } = useCart();
  const [isUpdated, setIsUpdated] = useState(false);
  const [newQuantity, setNewQuantity] = useState({});  // Track new quantity for each item

  const handleQuantityChange = (id, type) => {
    updateQuantity(id, type);  // Call the updateQuantity from CartContext
    setIsUpdated(true);  // Set the flag to true when quantity changes
  };

  const handleInputChange = (e, id) => {
    const value = Math.max(1, parseInt(e.target.value) || 1);
    setNewQuantity(prev => ({ ...prev, [id]: value })); // Update the new quantity
    setIsUpdated(true);  // Mark as updated
  };

  const handleDelete = (id) => {
    removeFromCart(id);  // Call removeFromCart to remove product from cart
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleProceedToCheckout = () => {
    // Pass cart data to the CheckoutPage via state
    navigate('/checkout', { state: { cart } });  // Using navigate() instead of history.push()
  };

  const handleUpdateCart = () => {
    // Perform necessary cart update logic if any
    console.log('Cart updated');
    setIsUpdated(false); // Reset the update flag after update
  };

  return (
    <div>
      <Header />
      <div className="container">
        <h1 className="text-center my-4">Cart</h1>
        <div className="row">
          {cart.map(item => (
            <div className="col-md-6 mb-4" key={item.id}>
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p>Price: ${item.price}</p>

                  {/* Quantity Controls - Flexbox to align horizontally */}
                  <div className="d-flex justify-content-start align-items-center">
                    <button className="btn btn-danger" onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
                    <input
                      type="number"
                      className="form-control w-25 mx-2"
                      value={newQuantity[item.id] || item.quantity}
                      onChange={(e) => handleInputChange(e, item.id)}
                      min="1"
                    />
                    <button className="btn btn-success" onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
                  </div>
                  <p>Subtotal: ${item.price * (newQuantity[item.id] || item.quantity)}</p>

                  {/* Delete Button */}
                  <button 
                    className="btn btn-danger mt-2"
                    onClick={() => handleDelete(item.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Total and Proceed to Checkout */}
        <div className="d-flex justify-content-between">
          <h3>Total: ${calculateTotal()}</h3>
          <div>
            <button 
              className="btn btn-primary mt-3" 
              onClick={handleUpdateCart}
              disabled={!isUpdated} // Disable button if no changes were made
            >
              Update Cart
            </button>
            <button
              className="btn btn-success mt-3 ml-2"
              onClick={handleProceedToCheckout}
            >
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}


export default CartPage;
