import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';  // Đảm bảo bạn đã cài Bootstrap

const products = [
  { id: 1, name: 'Sản phẩm 1', price: 100, description: 'Mô tả sản phẩm 1', imageUrl: 'image1.jpg' },
  { id: 2, name: 'Sản phẩm 2', price: 150, description: 'Mô tả sản phẩm 2', imageUrl: 'image2.jpg' },
  // Thêm các sản phẩm khác nếu cần
];

function HomePage() {
  return (
    <div>
      <Header />
      <div className="container">
        <h1 className="text-center my-4">Welcome to Our Store</h1>
        <div className="row">
          {products.map(product => (
            <div className="col-md-3 mb-4" key={product.id}>
              <div className="card">
                <img src={product.imageUrl} className="card-img-top" alt={product.name} />
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">${product.price}</p>
                  <button className="btn btn-success">Add to Cart</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default HomePage;
